import React, { Component } from 'react';
import { connect } from 'react-redux';
import { addProject } from '../Actions/index.js';
import { toneAnalyzer } from '../tone.js';

class TextForm extends Component {

  constructor() {
    super();

    this.state = {
      text: '',
      result: ''
    };
  }

  updateProject(result) {
    this.setState({result: result});
    return new Promise(
      (resolve,reject) => {
      resolve(this.state)
    })
  }



  getResult = (project) => {
      let toneUsername= "5be14bed-d818-4bd1-b4ca-e8b09dcc73b8";
      let tonePassword= "THTqBjmyU6k4";
      let toneUrl = "https://gateway.watsonplatform.net/tone-analyzer/api/v3/tone?version=2017-09-21";
      let data = {};
      data.text = project.text
      let request = require("request");

      return new Promise(
        (resolve, reject) => {
         request.post(
           {
             url: toneUrl,
             json: data,
             auth: {
               user: toneUsername,
               pass: tonePassword
             }
           }, function(err, res, data) {
                if (err) reject(err);

                resolve(data);
              })
        }
          // closing Promise
      );
      // end of getResult
    };

  handleOnSubmit = event => {
    event.preventDefault();
    const { addProject, history } = this.props;
    this.getResult(this.state).then(data => this.updateProject(data)).then(project => addProject(project)).then(history.push('/result'))
  }

  handleOnChange = event => {
    this.setState({
      text: event.target.value
    });
  }

  render(){
    return (
      <form style={{ marginTop: '16px' }} onSubmit={this.handleOnSubmit} >
        <textarea name="text" cols="40" rows="5"
          onChange={this.handleOnChange}
          placeholder="Paste text here">
        </textarea>
        <input type="submit" value="Submit" />
      </form>
    );
  }
}

const mapDispatchToProps = dispatch => {
  return {
    addProject: (project) => dispatch(addProject(project))
  };
};


export default connect(null, mapDispatchToProps)(TextForm)
